SELECT 'INSERT INTO control.spctl_data_source_tables(table_type,table_name) VALUES (''WLA'','''||table_schema||'.'||table_name||''');'
  FROM information_schema.tables
 WHERE table_schema IN ('adm','adnetwork','adsops','adstraffic','dbclk','evttracker')
   AND table_type='BASE TABLE' AND table_name LIKE '%weekly%' ORDER BY table_schema,table_name

SELECT 'INSERT INTO control.spctl_data_source_tables(table_type,table_name) VALUES (''DIM'','''||table_schema||'.'||table_name||''');'
  FROM information_schema.tables
 WHERE table_schema IN ('refer')
   AND table_type='BASE TABLE' 
   --AND table_name LIKE '%weekly%' 
   ORDER BY table_schema,table_name


